for init in {200,50,500}; do for rate in {"2.0",1.2,"5.0"}; do for mutType in {"INDEL","SNP"}; do for file in ../data/trajexp${rate}.init${init}.mutType${mutType}/*; do  echo $file; filename="${file##*/}"; echo $filename;  ../timeSeriesCode/selection-master/sr -D $file -n 100000 -d 0.005 -F 20 -f 1000 -s 1000 -P ../data/pop.hist.exp${rate}.init${init}.txt -e 8067 -o ../MCMCout/MCMCinit${init}rate${rate}mutType${mutType}/$filename ; gzip ../MCMCout/MCMCinit${init}rate${rate}mutType${mutType}/$filename ; done; done; done; done
